<?php\n// Set the session max lifetime to 30 minutes (1800 seconds)\nini_set('session.gc_maxlifetime', 1800);\n\n// Start the session\nsession_start();\n\n// Check if the session is new or has expired\nif (isset($_SESSION['CREATED']) && (time() - $_SESSION['CREATED'] > 1800)) {\n    // Session started more than 30 minutes ago\n    session_unset();     // Unset $_SESSION variable\n    session_destroy();   // Destroy the session\n    session_start();     // Start a new session\n    $_SESSION['CREATED'] = time(); // Set creation time for the new session\n} elseif (!isset($_SESSION['CREATED'])) {\n    // Session is new\n    $_SESSION['CREATED'] = time();\n}\n\n// Your application logic here\n\n?>