<?php\nfunction imageToHex($imagePath) {\n    $imageData = file_get_contents($imagePath);\n    return bin2hex($imageData);\n}\n\nfunction insertImageToRtf($rtfFilePath, $imagePath, $placeholder) {\n    $rtfContent = file_get_contents($rtfFilePath);\n    $imageHexData = imageToHex($imagePath);\n\n    $rtfImageData = "{\\pict\\jpegblip\\picwgoal1000\\pichgoal1000\n" . chunk_split($imageHexData, 64, "\n") . "}";\n\n    $newRtfContent = str_replace($placeholder, $rtfImageData, $rtfContent);\n\n    file_put_contents($rtfFilePath, $newRtfContent);\n}\n\n$rtfFilePath = 'path/to/your/file.rtf';\n$imagePath = 'path/to/your/image.jpg';\n$placeholder = '{photo}';\n\ninsertImageToRtf($rtfFilePath, $imagePath, $placeholder);\n?>