$xml = <<<XML\n<root>\n  <thing>\n    <specs>\n      <spec1 />\n      <spec3 />\n      <spec2 />\n    </specs>\n    <details />\n    <more_info>\n      <info1 />\n      <info2 />\n    </more_info>\n  </thing>\n</root>\nXML;\n\n$reader = new XMLReader();\n$reader->xml($xml);\n\n$specs = [];\n\nwhile ($reader->read()) {\n    if ($reader->nodeType == XMLReader::ELEMENT && $reader->localName == 'specs') {\n        while ($reader->read()) {\n            if ($reader->nodeType == XMLReader::ELEMENT && in_array($reader->localName, ['spec1', 'spec2', 'spec3'])) {\n                $specs[] = $reader->localName;\n            }\n            if ($reader->nodeType == XMLReader::END_ELEMENT && $reader->localName == 'specs') {\n                break;\n            }\n        }\n    }\n}\n\n$reader->close();\n\n// Output the specs\nforeach ($specs as $spec) {\n    echo $spec . "\n";\n}