<?php\n// Define the pattern to search for files\n$pattern = 'path/to/files/DFectuoso_*.doc';\n$files = glob($pattern);\n\nif ($files) {\n    foreach ($files as $file) {\n        // Display each file as a link\n        $filename = basename($file);\n        echo '<a href="' . htmlspecialchars($file) . '">' . htmlspecialchars($filename) . '</a><br>';\n    }\n} else {\n    echo 'No files found.';\n}\n?>