function getLastLine($filePath) {\n    $file = fopen($filePath, "r");\n    $pos = -1;\n    $lastLine = '';\n\n    if ($file === false) {\n        return false;\n    }\n\n    fseek($file, $pos, SEEK_END);\n    $char = fgetc($file);\n\n    if ($char === false || $char === "\n") {\n        // Handle empty file or file with only a newline\n        fclose($file);\n        return '';\n    }\n\n    while ($char !== "\n" && fseek($file, $pos, SEEK_END) !== -1) {\n        $lastLine = $char . $lastLine;\n        $pos--;\n        $char = fgetc($file);\n    }\n\n    fclose($file);\n    return $lastLine;\n}\n\n// Usage\n$lastLine = getLastLine("path/to/your/logfile.log");\necho $lastLine;