<?php echo '<?xml version="1.0" encoding="' . $encoding . '"?>'; ?>\n<rss version="2.0">\n<channel>\n    <title><?php echo $feed_name; ?></title>\n    <link><?php echo $feed_url; ?></link>\n    <description><?php echo $page_description; ?></description>\n    <language><?php echo $page_language; ?></language>\n    <creator><?php echo $creator_email; ?></creator>\n\n    <?php foreach($posts as $post): ?>\n    <item>\n        <title><?php echo xml_convert($post->title); ?></title>\n        <link><?php echo site_url('post/' . $post->id); ?></link>\n        <guid><?php echo site_url('post/' . $post->id); ?></guid>\n        <description><![CDATA[<?php echo $post->content; ?>]]></description>\n        <pubDate><?php echo date(DATE_RSS, strtotime($post->created_at)); ?></pubDate>\n    </item>\n    <?php endforeach; ?>\n\n</channel>\n</rss>