<?php\ndefined('BASEPATH') OR exit('No direct script access allowed');\n\nclass Rss extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        $this->load->model('rss_model');\n    }\n\n    public function index() {\n        $data['feed_name'] = 'Your Site Name';\n        $data['encoding'] = 'utf-8';\n        $data['feed_url'] = base_url('rss');\n        $data['page_description'] = 'Description of your site';\n        $data['page_language'] = 'en-en';\n        $data['creator_email'] = 'your-email@example.com';\n        $data['posts'] = $this->rss_model->get_posts();\n\n        header("Content-Type: application/rss+xml");\n        $this->load->view('rss_view', $data);\n    }\n}\n?>