$response = $item['response'];\n\n// Check if the response is a valid serialized string\nif (is_string($response) && preg_match('/^a:\d+:{.*}$/', $response)) {\n    $unserializedData = @unserialize($response);\n    if ($unserializedData === false && $response !== 'b:0;') {\n        echo "Error unserializing data.";\n    } else {\n        print_r($unserializedData);\n    }\n} else {\n    echo "Invalid serialized string.";\n}