<?php\nsession_start();\n\n// Check if the random number is already set in the session\nif (!isset($_SESSION['number'])) {\n    // generate a random number for user to guess\n    $_SESSION['number'] = rand(1, 100);\n}\n\n$number = $_SESSION['number'];\n\nif ($_POST) {\n    // grab the user input guess\n    $guess  = $_POST['guess'];\n\n    if ($guess < $number) {\n        echo "Guess Higher";\n    } elseif ($guess > $number) {\n        echo "Guess Lower";\n    } elseif ($guess == $number) {\n        echo "You got it!";\n        // Reset the number in session after a correct guess\n        unset($_SESSION['number']);\n    }\n    echo "<br />Random Number: " . $number . "<br />";\n    echo $guess;\n}\n?>\n\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml">\n<head>\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\n<title>Guess A Number</title>\n</head>\n\n<body>\n<form action="<?= $_SERVER['PHP_SELF'] ?>" method="post" name="guess-a-number">\n    <label for="guess">Guess A Number:</label><br/ >\n    <input type="text" name="guess" />\n    <input name="submit" type="submit" />\n</form>\n</body>\n</html>