<?php\n// Load the HTML content\n$html = '<option value="abc">Test - 123</option>\n<option value="def">Test - 456</option>\n<option value="ghi">Test - 789</option>';\n\n// Create a new DOMDocument instance\n$dom = new DOMDocument();\n\n// Suppress errors due to malformed HTML\nlibxml_use_internal_errors(true);\n\n// Load the HTML content into the DOMDocument\n$dom->loadHTML($html);\n\n// Restore error handling\nlibxml_clear_errors();\n\n// Create a new DOMXPath instance\n$xpath = new DOMXPath($dom);\n\n// Query for all <option> elements\n$options = $xpath->query('//option');\n\n// Iterate over the elements and output their text content\nforeach ($options as $option) {\n    echo $option->nodeValue . "\n";\n}\n?>