<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch the menu items\n$sql = "SELECT id, menu_title, parent_menu_id FROM menu ORDER BY parent_menu_id, weight";\n$result = $conn->query($sql);\n\n// Store the items into an array\n$menu = [];\nwhile($row = $result->fetch_assoc()) {\n    $menu[$row['parent_menu_id']][] = $row;\n}\n\n// Function to build the menu recursively\nfunction buildMenu($parent_id, $menu) {\n    if (isset($menu[$parent_id])) {\n        echo "<ul>";\n        foreach ($menu[$parent_id] as $item) {\n            echo "<li>" . $item['menu_title'];\n            buildMenu($item['id'], $menu);\n            echo "</li>";\n        }\n        echo "</ul>";\n    }\n}\n\n// Display the menu\necho '<nav>';\nbuildMenu(0, $menu);\necho '</nav>';\n\n// Close connection\n$conn->close();\n?>