<?php\nfunction function1($arg1, $arg2 = null) {\n    // Function implementation\n}\n\n$functionName = 'function1';\n$reflection = new ReflectionFunction($functionName);\n\n$parameters = $reflection->getParameters();\n\nforeach ($parameters as $parameter) {\n    echo 'Name: ' . $parameter->getName() . "\n";\n    echo 'Is optional: ' . ($parameter->isOptional() ? 'Yes' : 'No') . "\n";\n    echo 'Has default value: ' . ($parameter->isDefaultValueAvailable() ? 'Yes' : 'No') . "\n";\n    if ($parameter->isDefaultValueAvailable()) {\n        echo 'Default value: ' . $parameter->getDefaultValue() . "\n";\n    }\n    echo "\n";\n}\n?>