$thread_id = 1; // Example thread ID\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Query the post count\n$sql = "SELECT post_count FROM threads WHERE id = ?";\n$stmt = $conn->prepare($sql);\n$stmt->bind_param('i', $thread_id);\n$stmt->execute();\n$stmt->bind_result($post_count);\n$stmt->fetch();\n$stmt->close();\n$conn->close();\n\necho "Number of posts in thread: " . $post_count;