<?php\nheader("Expires: Mon, 26 Jul 1997 05:00:00 GMT");\nheader("Last-Modified: " . gmdate("D, d M Y H:i:s") . " GMT");\nheader("Cache-Control: no-store, no-cache, must-revalidate");\nheader("Cache-Control: post-check=0, pre-check=0", false);\nheader("Pragma: no-cache");\nheader("Content-Type: text/plain");\n\nsession_start();\n\n// include main file\nrequire_once($_SESSION["FILEROOT"] . "xsite/init.php");\n\n// check if formulier is posted\n$zoekterm = C_GPC::getGETVar("s");\n$s = "";\n\nif ($zoekterm != "") {\n    $query = "SELECT number, name, city, zib, zip_annex FROM articles WHERE version='edit' AND (name LIKE '%$zoekterm%' OR brinnummer='$zoekterm') ORDER BY name";\n    if ($rs = C_DB::fetchRecordSet($query)) {\n        while ($row = C_DB::fetchRow($rs)) {\n            if ($row["city"] != "") {\n                $s .= $row["name"] . ", " . $row["city"] . "|" . $row["number"] . "\n";\n            } else {\n                $s .= $row["name"] . ", " . $row["zip"] . $row["zip_annex"] . "|" . $row["number"] . "\n";\n            }\n        }\n    }\n}\n\necho $s;\n?>