<?php\n$html = <<<HTML\n<table>\n    <tr>\n        <td>quote1</td>\n        <td>have you trying it off and on again ?</td>\n    </tr>\n    <tr>\n        <td>quote65</td>\n        <td>You wouldn't steal a helmet of a policeman</td>\n    </tr>\n</table>\nHTML;\n\n$doc = new DOMDocument();\n@$doc->loadHTML($html);\n$xpath = new DOMXPath($doc);\n\n$rows = $xpath->query('//table/tr');\nforeach ($rows as $row) {\n    $cells = $row->getElementsByTagName('td');\n    if ($cells->length == 2) {\n        $quote = $cells->item(0)->nodeValue;\n        $text = $cells->item(1)->nodeValue;\n        echo "$quote:$text\n";\n    }\n}\n?>