function markThreadAsRead($userID, $threadID) {\n    $currentDateTime = date('Y-m-d H:i:s');\n    $sql = "INSERT INTO user_thread_read_status (UserID, ThreadID, LastReadDateTime)\n            VALUES (?, ?, ?)\n            ON DUPLICATE KEY UPDATE LastReadDateTime = ?";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$userID, $threadID, $currentDateTime, $currentDateTime]);\n}