<!-- process_form.php -->\n<?php\n// Check if checkbox 'option1' is set\n$option1 = isset($_POST['option1']) ? $_POST['option1'] : 0;\n\n// Check if checkbox 'option2' is set\n$option2 = isset($_POST['option2']) ? $_POST['option2'] : 0;\n\n// Check if 'question' is set\n$question = isset($_POST['question']) ? $_POST['question'] : '';\n\n// Now you can safely use $option1, $option2, and $question\n// Example: Insert into MySQL database\n\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "INSERT INTO your_table (option1, option2, question) VALUES ('$option1', '$option2', '$question')";\n\nif ($conn->query($sql) === TRUE) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $sql . "<br>" . $conn->error;\n}\n\n$conn->close();\n?>