function deleteUsers($userIDs) {\n    // Check if $userIDs is an array and not empty\n    if (!is_array($userIDs) || empty($userIDs)) {\n        return 'no';\n    }\n\n    // Create a connection with PDO (make sure to replace with your database details)\n    $dsn = 'mysql:host=your_host;dbname=your_dbname';\n    $username = 'your_username';\n    $password = 'your_password';\n\n    try {\n        $pdo = new PDO($dsn, $username, $password);\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Create placeholders for each userID\n        $placeholders = implode(',', array_fill(0, count($userIDs), '?'));\n\n        // Prepare the DELETE statement\n        $query = "DELETE FROM members WHERE member_id IN ($placeholders)";\n        $stmt = $pdo->prepare($query);\n\n        // Execute the statement with the userIDs as parameters\n        $stmt->execute($userIDs);\n\n        return 'yes';\n    } catch (PDOException $e) {\n        // Handle any errors\n        return 'no';\n    }\n}