<?php\n$threadID = $_GET['threadID'];\n\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// SQL query to fetch posts and user information\n$sql = "\n    SELECT \n        p.id AS postID, \n        p.content, \n        f.signature, \n        f.forumRank, \n        f.numberOfPosts\n    FROM \n        Posts p\n    JOIN \n        ForumSettings f ON p.userID = f.userID\n    WHERE \n        p.threadID = ?";\n\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("i", $threadID);\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Display posts with user information\nwhile ($row = $result->fetch_assoc()) {\n    echo "<div class='post'>";\n    echo "<p>" . htmlspecialchars($row['content']) . "</p>";\n    echo "<div class='user-info'>";\n    echo "<p>Signature: " . htmlspecialchars($row['signature']) . "</p>";\n    echo "<p>Forum Rank: " . htmlspecialchars($row['forumRank']) . "</p>";\n    echo "<p>Number of Posts: " . htmlspecialchars($row['numberOfPosts']) . "</p>";\n    echo "</div>";\n    echo "</div>";\n}\n\n$stmt->close();\n$mysqli->close();\n?>