class View {\n    public function show($file, $variables = []) {\n        // Extract variables to the current symbol table\n        extract($variables);\n\n        // Start output buffering\n        ob_start();\n\n        // Include the file\n        include($file . ".php");\n\n        // Get the contents of the buffer and end buffering\n        $content = ob_get_clean();\n\n        // Output the content\n        echo $content;\n    }\n}