<?php\nclass Login\n{\n    private static $_auth;\n\n    public function __construct($username, $rawPassword) {\n        global $db;\n\n        require('edit/users/config.php');\n\n        // Hash the password with a salt\n        $hashedPassword = sha1(SALT . $rawPassword);\n\n        // Sanitize and query the database\n        $query = 'SELECT firstname FROM users WHERE user = "' . $db->cleanString($username) . '" AND pass = "' . $db->cleanString($hashedPassword) . '" LIMIT 1';\n        $login = $db->query($query);\n\n        if ($login) {\n            // Set session variables\n            $_SESSION['username'] = $username;\n            self::$_auth = true;\n            $_SESSION['auth'] = true; // Store authentication status in session\n\n            // Redirect to base directory\n            header('Location: ' . CONFIG_DIR_BASE);\n        } else {\n            // Handle login error\n            ErrorHandler::addErrorToStack('Your username and/or password did not match one in our system.');\n        }\n    }\n\n    public static function isAuthenticated() {\n        // Check if auth session variable is set and true\n        return isset($_SESSION['auth']) && $_SESSION['auth'] === true;\n    }\n}\n\n// Usage example\nif (Login::isAuthenticated()) {\n    echo '<a href="/sign-out/">Sign Out</a>';\n}