<?php\nheader('Content-Type: text/event-stream');\nheader('Cache-Control: no-cache');\n\n$cmd = 'your-shell-command-here';  // Replace with your actual shell command\n\n$handle = popen($cmd, 'r');\nif ($handle) {\n    while (!feof($handle)) {\n        $line = fgets($handle);\n        if (preg_match('/\[\S+\]\s+(\d+\.\d+)%/', $line, $matches)) {\n            $percentage = $matches[1];\n            echo "data: {$percentage}\n\n";\n            ob_flush();\n            flush();\n        }\n        usleep(50000);  // Sleep for 50ms to avoid high CPU usage\n    }\n    pclose($handle);\n}\n?>