class IndexController extends Zend_Controller_Action\n{\n    public function __call($method, $args)\n    {\n        // Check if a view script exists for the requested action\n        $viewScript = $this->getRequest()->getActionName() . '.phtml';\n        if ($this->view->getScriptPath($viewScript)) {\n            // Render the view script if it exists\n            $this->render($viewScript);\n        } else {\n            // Redirect to 404 page if the view script does not exist\n            $this->_redirect('/error/not-found');\n        }\n    }\n}