function setCookieInfo($data, $expiry = 0)\n{\n    if ($data === false) {\n        // Remove cookie by setting expiry time to the past\n        $cookie = false;\n        $expiry = time() - 3600; // 1 hour in the past\n    } else {\n        $serial = base64_encode(serialize($data));\n        // Ensure $XXX contains a proper value or variable\n        $hash = md5($XXX);\n        $cookie = $hash . "---" . $serial;\n    }\n\n    if ($_SERVER['SERVER_NAME'] == 'localhost') {\n        $domain = null;\n    } else {\n        $domain = '.' . $_SERVER['SERVER_NAME'];\n    }\n\n    return setcookie('Auth', $cookie, $expiry, '/', $domain);\n}