function displayCart($dbConn) {\n    if (!isset($_SESSION['cart'])) {\n        echo "Your cart is empty.";\n        return;\n    }\n\n    $cart = $_SESSION['cart'];\n    foreach ($cart as $productId => $quantity) {\n        $stmt = $dbConn->prepare("SELECT name, price FROM products WHERE id = ?");\n        $stmt->bind_param("i", $productId);\n        $stmt->execute();\n        $result = $stmt->get_result();\n        $product = $result->fetch_assoc();\n\n        echo "Product: " . htmlspecialchars($product['name']) . "<br>";\n        echo "Quantity: " . htmlspecialchars($quantity) . "<br>";\n        echo "Price: $" . htmlspecialchars($product['price']) . "<br><br>";\n    }\n}