use Laminas\Mail\Message;\nuse Laminas\Mail\Transport\Sendmail;\nuse Laminas\Mime\Message as MimeMessage;\nuse Laminas\Mime\Mime;\nuse Laminas\Mime\Part as MimePart;\n\n// Create the email message\n$message = new Message();\n$message->addFrom('sender@example.com')\n        ->addTo('recipient@example.com')\n        ->setSubject('Email with Inline Image');\n\n// Create the text and HTML parts\n$text = new MimePart('This is the text part of the email.');\n$text->type = Mime::TYPE_TEXT;\n\n$html = new MimePart('<html><body><p>This is the HTML part of the email.</p><img src="cid:logo"></body></html>');\n$html->type = Mime::TYPE_HTML;\n\n// Create the inline image part\n$image = new MimePart(fopen('/path/to/image.jpg', 'r'));\n$image->type = 'image/jpeg';\n$image->disposition = Mime::DISPOSITION_INLINE;\n$image->encoding = Mime::ENCODING_BASE64;\n$image->filename = 'image.jpg';\n$image->id = 'logo';\n\n// Create the MIME message and set its parts\n$body = new MimeMessage();\n$body->setParts([$text, $html, $image]);\n\n// Set the MIME message as the email body\n$message->setBody($body);\n\n// Send the email\n$transport = new Sendmail();\n$transport->send($message);