$categoryName = $_POST['category_name'];\n$documentTitle = $_POST['document_title'];\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Securely fetch the category ID for the given category name\n$stmt = $conn->prepare("SELECT id FROM categories WHERE category_name = ?");\n$stmt->bind_param("s", $categoryName);\n$stmt->execute();\n$stmt->bind_result($categoryId);\n$stmt->fetch();\n$stmt->close();\n\nif ($categoryId) {\n    // Securely insert the document title linked to the category ID\n    $stmt = $conn->prepare("INSERT INTO documents (document_title, category_id) VALUES (?, ?)");\n    $stmt->bind_param("si", $documentTitle, $categoryId);\n    $stmt->execute();\n    $stmt->close();\n\n    echo "Document title has been saved successfully.";\n} else {\n    echo "Category not found.";\n}\n\n$conn->close();