$categoryName = $_POST['category_name'];\n$documentTitle = $_POST['document_title'];\n\ntry {\n    $pdo = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Securely fetch the category ID for the given category name\n    $stmt = $pdo->prepare("SELECT id FROM categories WHERE category_name = ?");\n    $stmt->execute([$categoryName]);\n    $categoryId = $stmt->fetchColumn();\n\n    if ($categoryId) {\n        // Securely insert the document title linked to the category ID\n        $stmt = $pdo->prepare("INSERT INTO documents (document_title, category_id) VALUES (?, ?)");\n        $stmt->execute([$documentTitle, $categoryId]);\n\n        echo "Document title has been saved successfully.";\n    } else {\n        echo "Category not found.";\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n$pdo = null;