<?php\n// Log the download information\n$logFile = 'downloads.log';\n$clientIP = $_SERVER['REMOTE_ADDR'];\n$timestamp = date('Y-m-d H:i:s');\n$logEntry = "Downloaded by: $clientIP at $timestamp\n";\nfile_put_contents($logFile, $logEntry, FILE_APPEND);\n\n// Set headers to serve the PostScript file\nheader('Content-type: application/postscript');\nheader('Content-Disposition: inline; filename="appendix.ps"');\n\n// Read and output the PostScript file\n$psFile = 'appendix.ps';\nreadfile($psFile);\nexit;\n?>