<?php\n// Database connection (replace with your actual connection details)\n$mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to fetch all items\n$item_query = "SELECT * FROM table_items";\n$item_result = $mysqli->query($item_query);\n\nif ($item_result->num_rows > 0) {\n    while ($item = $item_result->fetch_assoc()) {\n        // Display the item name\n        echo strtoupper($item['item_name']) . "\n";\n        echo str_repeat('-', strlen($item['item_name'])) . "\n";\n\n        // Query to fetch subitems for the current item\n        $subitem_query = "SELECT * FROM table_subitems WHERE item_id = " . $item['item_id'];\n        $subitem_result = $mysqli->query($subitem_query);\n\n        if ($subitem_result->num_rows > 0) {\n            while ($subitem = $subitem_result->fetch_assoc()) {\n                // Display the subitem name\n                echo $subitem['subitem_name'] . "\n";\n            }\n        } else {\n            echo "No subitems found\n";\n        }\n        echo "\n"; // Newline for better formatting\n    }\n} else {\n    echo "No items found";\n}\n\n$mysqli->close();\n?>