class ShoppingCart {\n       private $items = [];\n\n       public function addItem($item, $quantity) {\n           $this->items[$item] = isset($this->items[$item]) ? $this->items[$item] + $quantity : $quantity;\n       }\n\n       public function removeItem($item) {\n           unset($this->items[$item]);\n       }\n\n       public function getItems() {\n           return $this->items;\n       }\n   }