<?php\n  // Check and set style from cookie or POST request\n  if (isset($_COOKIE["selectedStyle"])) {\n    $style = $_COOKIE["selectedStyle"];\n  } else {\n    $style = 'spring'; // Default style\n  }\n\n  if (isset($_POST["changeStyle"])) {\n    $style = $_POST["changeStyle"];\n    setcookie("selectedStyle", $style, time() + (86400 * 30), "/"); // Expires in 30 days\n  }\n\n  // Check and set font size from cookie or POST request\n  if (isset($_COOKIE["selectedFontSize"])) {\n    $fontSize = $_COOKIE["selectedFontSize"];\n  } else {\n    $fontSize = 'standard'; // Default font size\n  }\n\n  if (isset($_POST["changeFontSize"])) {\n    $fontSize = $_POST["changeFontSize"];\n    setcookie("selectedFontSize", $fontSize, time() + (86400 * 30), "/"); // Expires in 30 days\n  }\n?>