<?php\n// Define the RRD file path\n$rrdFile = "example.rrd";\n\n// Create the RRD database\n$createCommand = "rrdtool create $rrdFile --step 300 "\n               . "DS:mydata:GAUGE:600:U:U "\n               . "RRA:AVERAGE:0.5:1:600 "\n               . "RRA:AVERAGE:0.5:6:700 "\n               . "RRA:AVERAGE:0.5:24:775 "\n               . "RRA:AVERAGE:0.5:288:797";\n\n// Execute the command\nexec($createCommand, $output, $returnVar);\n\n// Check the result\nif ($returnVar == 0) {\n    echo "RRD file created successfully.";\n} else {\n    echo "Failed to create RRD file.";\n}\n?>