function parseCSV($csvString) {\n    // Split the CSV string into individual fields\n    $fields = preg_split('/,(?=(?:[^"]*"[^"]*")*[^"]*$)/', trim($csvString));\n\n    // Process each field\n    $parsedFields = array_map(function($field) {\n        // Remove enclosing double quotes\n        $field = trim($field, '"');\n\n        // Replace doubled double quotes with a single double quote\n        $field = str_replace('""', '"', $field);\n\n        return $field;\n    }, $fields);\n\n    return $parsedFields;\n}\n\n// Example usage\n$csvString = '"My ""other"" cat", "Another ""example"""';\n$parsedFields = parseCSV($csvString);\n\nprint_r($parsedFields);