class SessionManager {\n       public function __construct() {\n           session_start();\n       }\n\n       public function set($key, $value) {\n           $_SESSION[$key] = $value;\n       }\n\n       public function get($key) {\n           return isset($_SESSION[$key]) ? $_SESSION[$key] : null;\n       }\n\n       public function destroy() {\n           session_destroy();\n       }\n   }