$texts = [\n    "give 4 cars",\n    "ga 5 cars",\n    "GA 5 Cars @mustang six exhausts are necessary",\n    "Give -1 Cars @mustang",\n    "Give Cars @mustang",\n    "Give 3 Cars @ford",\n    "Give 5 Cars @cobra_gt"\n];\n\nforeach ($texts as $text) {\n    if (preg_match('/(?:Give|GA) (\d+) Cars @(\w+)/i', $text, $output)) {\n        print_r($output);\n    } else {\n        echo "No match for: $text\n";\n    }\n}