<?php\n$login = $_POST['login'];\n$password = $_POST['password'];\n\n$mysqli = new mysqli('localhost', 'root', 'abc123', 'aun_vox');\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("SELECT password FROM customer WHERE login = ?");\n$stmt->bind_param("s", $login);\n$stmt->execute();\n$stmt->bind_result($db_pass);\n$stmt->fetch();\n$stmt->close();\n\nif ($db_pass) {\n    if (password_verify($password, $db_pass)) {\n        echo "You did it.";\n    } else {\n        echo "Wrong.";\n    }\n} else {\n    echo "Wrong.";\n}\n\n$mysqli->close();\n?>