function showFields($selClient) {\n    // Create a connection to the database (replace with your own connection details)\n    $conn = new mysqli("hostname", "username", "password", "database");\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Prepare and execute the query\n    $stmt = $conn->prepare("SELECT clientname, prospect, salesperson FROM project WHERE projectid = ?");\n    $stmt->bind_param("i", $selClient);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    $values = array();\n    while ($row = $result->fetch_assoc()) {\n        $values[] = array($row['clientname'], $row['prospect'], $row['salesperson']);\n    }\n\n    // Close the connection\n    $stmt->close();\n    $conn->close();\n\n    return $values;\n}