<?php\n\n// Ensure the script runs long enough to download large files\nset_time_limit(300);\n\n// Get the remote file URL from query parameters\n$remoteFile = filter_input(INPUT_GET, 'url', FILTER_VALIDATE_URL);\n\nif (!$remoteFile) {\n    echo "<p>Invalid URL provided.</p>";\n    exit;\n}\n\n// Initialize a cURL session\n$ch = curl_init($remoteFile);\n\n// Set cURL options\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects if any\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true); // Verify SSL certificate\n\n// Execute the cURL session\n$data = curl_exec($ch);\n\n// Check for errors\nif (curl_errno($ch)) {\n    echo "<p>Error: " . curl_error($ch) . "</p>";\n    curl_close($ch);\n    exit;\n}\n\n// Get the HTTP status code\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n\n// Close the cURL session\ncurl_close($ch);\n\nif ($httpCode != 200) {\n    echo "<p>Failed to download file. HTTP Status Code: $httpCode</p>";\n    exit;\n}\n\n// Save the data to a local file\n$localFile = 'downloaded_file.mp4';\nif (file_put_contents($localFile, $data) === false) {\n    echo "<p>Failed to save the file.</p>";\n    exit;\n}\n\necho "<p>File downloaded and saved as $localFile</p>";\n\n?>