$templateContent = file_get_contents('path/to/template.php');\n   $pattern = '/@template\s+(\w+)|@content_area\s+(\w+):\s+(.+)/';\n   preg_match_all($pattern, $templateContent, $matches, PREG_SET_ORDER);\n   \n   $templateName = '';\n   $contentAreas = [];\n   \n   foreach ($matches as $match) {\n       if (!empty($match[1])) {\n           $templateName = $match[1];\n       } elseif (!empty($match[2]) && !empty($match[3])) {\n           $contentAreas[$match[2]] = $match[3];\n       }\n   }\n   \n   // Use $templateName and $contentAreas as needed