class My_Form extends Zend_Form\n{\n    public function init()\n    {\n        // Custom text field\n        $this->addElement('text', 'username', array(\n            'label'      => 'Username:',\n            'required'   => true,\n            'decorators' => array(\n                'ViewHelper',\n                array('Errors'),\n                array(array('data' => 'HtmlTag'), array('tag' => 'div', 'class' => 'element')),\n                array('Label', array('tag' => 'div', 'class' => 'label')),\n                array(array('row' => 'HtmlTag'), array('tag' => 'div', 'class' => 'form-row')),\n            ),\n        ));\n\n        // Insert custom HTML\n        $this->addElement(new My_Form_Element_TextHtml('customHtml'));\n\n        // Custom email field with error message\n        $this->addElement('text', 'email', array(\n            'label'      => 'Email:',\n            'required'   => true,\n            'validators' => array('EmailAddress'),\n            'messages'   => array(\n                Zend_Validate_EmailAddress::INVALID => 'Custom error message for invalid email address.'\n            ),\n        ));\n\n        // Custom button\n        $this->addElement(new My_Form_Element_CustomButton('myButton'));\n\n        // Set form decorators\n        $this->setDecorators(array(\n            'FormElements',\n            'Form',\n        ));\n    }\n}