defined('_JEXEC') or die('Restricted access');\n\nclass MyComponentControllerSearch extends JControllerLegacy {\n    public function search() {\n        $query = JFactory::getApplication()->input->getString('q');\n        $result = $this->searchAnotherSite($query);\n        \n        // Do something with the search result, like rendering it in a view\n        $view = $this->getView('search', 'html');\n        $view->assignRef('result', $result);\n        $view->display();\n    }\n\n    private function searchAnotherSite($query) {\n        $url = "https://example.com/search";\n        $params = http_build_query(['q' => $query]);\n\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $url . '?' . $params);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n        \n        $response = curl_exec($ch);\n        \n        if (curl_errno($ch)) {\n            echo 'Error:' . curl_error($ch);\n        }\n        \n        curl_close($ch);\n        \n        return $response;\n    }\n}