<?php \nif(!isset($_SESSION['user'])) {\n    if(isset($error)) echo htmlspecialchars($error);\n    echo '<form action="' . htmlspecialchars($_SERVER["PHP_SELF"]) . '" method="post">\n        <label>Username: </label>\n        <input type="text" name="userName" value="' . (isset($_POST['userName']) ? htmlspecialchars($_POST['userName']) : '') . '" /><br />\n        <label>Password: </label>\n        <input type="password" name="password" />\n        <input type="submit" value="Login" class="button" />\n        <ul class="sidemenu">\n        <li><a href="register.php">Register</a></li>\n        <li><a href="forgotPassword.php">Forgot Password</a></li>\n    </ul>\n    </form>';\n} else {\n    echo '<ul class="sidemenu">\n        <li>' . htmlspecialchars($_SESSION['user']) . '</li>\n        <li><a href="' . htmlspecialchars($_SERVER["PHP_SELF"]) . '?logout=true">Logout</a></li>\n        </ul>';\n}\n?>