function countMatches($needle, $haystack) {\n    $results = array();\n    \n    foreach ($haystack as $hay) {\n        $count = 0;\n        $hay = strtolower($hay);\n        \n        foreach ($needle as $nee) {\n            $nee = strtolower($nee);\n            // Use preg_match to ensure we only match the beginning of words\n            if (preg_match('/\b' . preg_quote($nee, '/') . '/i', $hay)) {\n                $count++;\n            }\n        }\n        \n        $results[$hay] = $count;\n    }\n    \n    return $results;\n}\n\n$needle = array('ba', 'hot', 'resta');\n$haystack = array(\n    'Southern Hotel',\n    'Grange Restaurant & Hotel',\n    'Austral Hotel',\n    'Barsmith Hotel',\n    'Errestas'\n);\n\n$result = countMatches($needle, $haystack);\nprint_r($result);