$ch = curl_init();\n\ncurl_setopt($ch, CURLOPT_URL, "https://secure.paymentgateway.com/blah.php");\ncurl_setopt($ch, CURLOPT_USERAGENT, "Mozilla/4.0 (compatible; MSIE 5.01; Windows NT 5.0)");\ncurl_setopt($ch, CURLOPT_VERBOSE, 1);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Ensure SSL verification\ncurl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 1);\n\n// Path to your CA bundle (you can download the latest CA bundle from https://curl.se/docs/caextract.html)\ncurl_setopt($ch, CURLOPT_CAINFO, getcwd() . '/cert/ca.crt');\n\n// Path to your client SSL certificate and key\ncurl_setopt($ch, CURLOPT_SSLCERT, getcwd() . '/cert/mycert.pem');\ncurl_setopt($ch, CURLOPT_SSLKEY, getcwd() . '/cert/mykey.pem');\ncurl_setopt($ch, CURLOPT_SSLCERTPASSWD, 'password');\n\n// POST data\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, "variables...");\n\n$output = curl_exec($ch);\n\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n} else {\n    echo $output;\n}\n\ncurl_close($ch);