function getUnreadThreads($userID, $forumID) {\n    $sql = "SELECT t.ThreadID, t.ThreadName, t.LastPostDateTime\n            FROM threads t\n            LEFT JOIN user_thread_read_status utrs \n            ON t.ThreadID = utrs.ThreadID AND utrs.UserID = ?\n            WHERE t.ForumID = ? AND (utrs.LastReadDateTime IS NULL OR t.LastPostDateTime > utrs.LastReadDateTime)";\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([$userID, $forumID]);\n    return $stmt->fetchAll(PDO::FETCH_ASSOC);\n}