// In Item.php (Model)\n     public function getItemWithDefaultImage($itemId) {\n         $item = $this->find('first', [\n             'conditions' => ['Item.id' => $itemId],\n             'contain' => ['Image']\n         ]);\n         if (!empty($item['Image'])) {\n             usort($item['Image'], function($a, $b) use ($item) {\n                 return ($a['id'] == $item['image_id']) ? -1 : 1;\n             });\n         }\n         return $item;\n     }