<?php\n// Connect to the database\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Array of common bot keywords\n$botKeywords = [\n    'bot', 'crawl', 'slurp', 'spider', 'mediapartners', 'Googlebot', 'Bingbot', 'Yahoo!', 'DuckDuckBot', 'Baiduspider', 'YandexBot', 'Sogou', 'Exabot', 'facebot', 'ia_archiver'\n];\n\n$count = 0;\n\n// Execute the query\n$query = "SELECT HTTP_USER_AGENT FROM your_table WHERE visit_date = CURDATE() - INTERVAL 1 DAY";\n$result = $mysqli->query($query);\n\nwhile ($row = $result->fetch_assoc()) {\n    $userAgent = strtolower($row['HTTP_USER_AGENT']);\n    $isBot = false;\n\n    foreach ($botKeywords as $keyword) {\n        if (strpos($userAgent, strtolower($keyword)) !== false) {\n            $isBot = true;\n            break;\n        }\n    }\n\n    if (!$isBot) {\n        $count++;\n    }\n}\n\necho "Total non-bot hits: " . $count;\n\n// Close connection\n$mysqli->close();\n?>