<?php\n// Array of common bot keywords\n$botKeywords = [\n    'bot', 'crawl', 'slurp', 'spider', 'mediapartners', 'Googlebot', 'Bingbot', 'Yahoo!', 'DuckDuckBot', 'Baiduspider', 'YandexBot', 'Sogou', 'Exabot', 'facebot', 'ia_archiver'\n];\n\n$count = 0;\n\nwhile ($row = mysql_fetch_array($yesterday, MYSQL_ASSOC)) {\n    $userAgent = strtolower($row['HTTP_USER_AGENT']);\n    $isBot = false;\n\n    foreach ($botKeywords as $keyword) {\n        if (strpos($userAgent, strtolower($keyword)) !== false) {\n            $isBot = true;\n            break;\n        }\n    }\n\n    if (!$isBot) {\n        $count++;\n    }\n}\n\necho "Total non-bot hits: " . $count;\n?>