<?php\n$somefile = 'path/to/your/file';\n$LastModified = filemtime($somefile);\n\n// Add 5 minutes (300 seconds) to the last modified time\n$LastModifiedPlusFiveMinutes = $LastModified + 300;\n\n// Get the current time\n$CurrentTime = time();\n\n// Compare and delete the file if the condition is met\nif ($LastModifiedPlusFiveMinutes <= $CurrentTime) {\n    if (unlink($somefile)) {\n        echo "File deleted successfully.";\n    } else {\n        echo "Error deleting the file.";\n    }\n} else {\n    echo "File does not need to be deleted yet.";\n}\n?>